for k = 1 : 4
    X1 = [edges{V.NumNodes}(k+1, 1), edges{V.NumNodes}(k+1, 2), edges{V.NumNodes}(k+1, 3)];
    X2 = [edges{V.NumNodes}(k, 1),   edges{V.NumNodes}(k, 2),   edges{V.NumNodes}(k, 3)];
    r =  [edges{V.NumNodes}(k+1, 4), edges{V.NumNodes}(k, 4)];

    nn = 5;
    cyl_color = 'b';
    closed = 0;
    Draw_Cone(X1, X2, r, nn, cyl_color, closed, 0);
    hold on;
end

draw_airwayTree_II (edges, V.Pointers, V.NumNodes, V.NumLeaves);
pause(0.1);



